import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../../api/axiosConfig";

function EditarBolso() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    modelo: "",
    marca: "",
    color: "",
    tamanio: "",
    precio: 0,
    materiales: []
  });

  const [marcas, setMarcas] = useState([]);
  const [materiales, setMateriales] = useState([]);

  useEffect(() => {
    api.get(`/bolsos/${id}`).then(res => setForm(res.data));
    api.get("/marcas/all-marcas").then(res => setMarcas(res.data));
    api.get("/materiales/all-materiales").then(res => setMateriales(res.data));
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const toggleMaterial = (tipo) => {
    setForm(prev => ({
      ...prev,
      materiales: prev.materiales.includes(tipo)
        ? prev.materiales.filter(m => m !== tipo)
        : [...prev.materiales, tipo]
    }));
  };

  const editar = (e) => {
    e.preventDefault();

    api.put(`/bolsos/editar/${id}`, form)
      .then(() => {
        alert("Bolso actualizado");
        navigate("/bolsos");
      })
      .catch(err => {
        console.error(err);
        alert("Error actualizando bolso");
      });
  };

  return (
    <div style={{ maxWidth: "700px", margin: "0 auto" }}>
      <h2>Editar Bolso</h2>

      <form onSubmit={editar}>
        <label>Modelo:</label>
        <input name="modelo" value={form.modelo} onChange={handleChange} required />

        <label>Marca:</label>
        <select name="marca" value={form.marca} onChange={handleChange} required>
          <option value="">Seleccione...</option>
          {marcas.map(m => (
            <option key={m.id} value={m.nombre}>{m.nombre}</option>
          ))}
        </select>

        <label>Color:</label>
        <input name="color" value={form.color} onChange={handleChange} />

        <label>Tama√±o:</label>
        <input name="tamanio" value={form.tamanio} onChange={handleChange} />

        <label>Precio:</label>
        <input type="number" name="precio" value={form.precio} onChange={handleChange} />

        <label>Materiales:</label>
        <div>
          {materiales.map(mat => (
            <label key={mat.id} style={{ display: "block" }}>
              <input
                type="checkbox"
                checked={form.materiales.includes(mat.tipo)}
                onChange={() => toggleMaterial(mat.tipo)}
              />
              {mat.tipo}
            </label>
          ))}
        </div>

        <button type="submit">Guardar cambios</button>
      </form>
    </div>
  );
}

export default EditarBolso;
