import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../../api/axiosConfig";

function EditarMaterial() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [form, setForm] = useState({
    tipo: "",
    precio: ""
  });

  const [mensaje, setMensaje] = useState("");

  useEffect(() => {
    api.get(`/materiales/${id}`)
      .then(response => {
        setForm({
          tipo: response.data.tipo,
          precio: response.data.precio
        });
      })
      .catch(error => {
        console.error("Error cargando material:", error);
      });
  }, [id]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje("");

    try {
      await api.put(`/materiales/editar/${id}`, form);

      setMensaje("Material actualizado");

      setTimeout(() => navigate(`/materiales/${id}`), 1000);

    } catch (error) {
      console.error("Error actualizando:", error);
      setMensaje("Error al actualizar");
    }
  };

  return (
    <div>
      <h2>Editar Material</h2>

      <form onSubmit={handleSubmit}>
        <label>Tipo:</label>
        <input 
          type="text" 
          name="tipo" 
          value={form.tipo} 
          onChange={handleChange} 
        />

        <label>Precio:</label>
        <input 
          type="number" 
          name="precio" 
          value={form.precio} 
          onChange={handleChange} 
        />

        <button type="submit">Guardar Cambios</button>
      </form>

      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

export default EditarMaterial;
