import React, { useEffect, useState } from "react";
import api from "../../api/axiosConfig";
import { Link } from "react-router-dom";

function MarcaList() {
  const [marcas, setMarcas] = useState([]);
  const [loading, setLoading] = useState(true);

  const cargarMarcas = () => {
    api.get("/marcas/all-marcas")
      .then(response => {
        setMarcas(response.data);
      })
      .catch(error => {
        console.error("Error cargando marcas:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  useEffect(() => {
    cargarMarcas();
  }, []);

  const eliminarMarca = (id) => {
    if (!window.confirm("¿Desea eliminar esta marca?")) return;

    api.delete(`/marcas/eliminar/${id}`)
      .then(() => {
        alert("Marca eliminada exitosamente");
        cargarMarcas();
      })
      .catch(error => {
        console.error("Error eliminando marca:", error);
        alert("No se pudo eliminar");
      });
  };

  if (loading) return <h3>Cargando marcas...</h3>;

  return (
    <div>
      <h2>Listado de Marcas</h2>

      <Link to="/marcas/crear">
        <button>Crear Nueva Marca</button>
      </Link>

      {marcas.length === 0 ? (
        <p>No hay marcas registradas.</p>
      ) : (
        <ul>
          {marcas.map(marca => (
            <li key={marca.id}>
              <strong>{marca.nombre}</strong>
              <br/>
              Bolsos: {marca.bolsos.length ? marca.bolsos.join(", ") : "Ninguno"}
              <br/>

              <Link to={`/marcas/editar/${marca.id}`}>
                <button>Editar</button>
              </Link>

              <button onClick={() => eliminarMarca(marca.id)}>
                Eliminar
              </button>

              <hr/>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MarcaList;
