import React, { useEffect, useState } from "react";
import api from "../../api/axiosConfig";
import { useParams, useNavigate } from "react-router-dom";

function EditarMarca() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [nombre, setNombre] = useState("");

  useEffect(() => {
    api.get(`/marcas/${id}`)
      .then(res => {
        setNombre(res.data.nombre);
      })
      .catch(err => {
        console.error(err);
        alert("Error cargando marca");
      });
  }, [id]);

  const editarMarca = (e) => {
    e.preventDefault();

    api.put(`/marcas/editar/${id}`, { nombre })
      .then(() => {
        alert("Marca actualizada");
        navigate("/marcas");
      })
      .catch(err => {
        console.error(err);
        alert("Error actualizando marca");
      });
  };

  return (
    <div>
      <h2>Editar Marca</h2>

      <form onSubmit={editarMarca}>
        <label>Nombre:</label>
        <input
          type="text"
          value={nombre}
          onChange={e => setNombre(e.target.value)}
          required
        />

        <button type="submit">Guardar Cambios</button>
      </form>
    </div>
  );
}

export default EditarMarca;
