import React, { useState } from "react";
import api from "../../api/axiosConfig";
import { useNavigate } from "react-router-dom";

function CrearMaterial() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    tipo: "",
    precio: ""
  });

  const [mensaje, setMensaje] = useState("");

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje("");

    try {
      const response = await api.post("/materiales/crear", form);
      console.log("Material creado:", response.data);

      setMensaje("Material creado exitosamente ✔");

      // Redirigir al listado luego de 1 segundo
      setTimeout(() => navigate("/materiales"), 1000);

    } catch (error) {
      console.error("Error creando material:", error);
      setMensaje("Error al crear el material");
    }
  };

  return (
    <div>
      <h2>Crear Material</h2>

      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", width: "300px" }}>
        <label>Tipo:</label>
        <input
          type="text"
          name="tipo"
          value={form.tipo}
          onChange={handleChange}
          required
        />

        <label>Precio:</label>
        <input
          type="number"
          name="precio"
          value={form.precio}
          onChange={handleChange}
          required
        />

        <button type="submit" style={{ marginTop: "10px" }}>
          Guardar
        </button>
      </form>

      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

export default CrearMaterial;
