import React, { useEffect, useState } from "react";
import api from "../../api/axiosConfig";

function MaterialesList() {
  const [materiales, setMateriales] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get("/materiales/all-materiales")
      .then(response => {
        setMateriales(response.data);
      })
      .catch(error => {
        console.error("Error cargando materiales:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <h3>Cargando materiales...</h3>;
  }

  return (
    <div>
      <h2>Listado de Materiales</h2>

      {materiales.length === 0 ? (
        <p>No hay materiales registrados.</p>
      ) : (
        <ul>
          {materiales.map(material => (
            <li key={material.id}>
              <strong>{material.tipo}</strong> â€” ${material.precio}
              <br />
              <em>Bolsos:</em> {material.bolsos.length > 0 ? material.bolsos.join(", ") : "Ninguno"}
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MaterialesList;
