import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../../api/axiosConfig";

function DetalleBolso() {
  const { id } = useParams();
  const [bolso, setBolso] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/bolsos/${id}`)
      .then(response => {
        setBolso(response.data);
      })
      .catch(error => {
        console.error("Error cargando bolso:", error);
      })
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <h3>Cargando bolso...</h3>;
  if (!bolso) return <p>No existe el bolso solicitado.</p>;

  return (
    <div style={{ maxWidth: "700px", margin: "0 auto" }}>
      <h2>Detalle del Bolso</h2>

      <p><strong>ID:</strong> {bolso.id}</p>
      <p><strong>Modelo:</strong> {bolso.modelo}</p>
      <p><strong>Marca:</strong> {bolso.marca}</p>
      <p><strong>Color:</strong> {bolso.color}</p>
      <p><strong>Tama√±o:</strong> {bolso.tamanio}</p>
      <p><strong>Precio:</strong> ${bolso.precio}</p>

      <p>
        <strong>Materiales:</strong><br />
        {bolso.materiales.length ? bolso.materiales.join(", ") : "Ninguno"}
      </p>

      <Link to="/bolsos">
        <button style={{ marginTop: "20px" }}>
          Volver al listado
        </button>
      </Link>
    </div>
  );
}

export default DetalleBolso;
