import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../../api/axiosConfig";

function DetalleMaterial() {
  const { id } = useParams();
  const [material, setMaterial] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/materiales/${id}`)
      .then(response => {
        setMaterial(response.data);
      })
      .catch(error => {
        console.error("Error obteniendo material:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [id]);

  if (loading) return <h3>Cargando...</h3>;

  if (!material) return <h3>No se encontró el material.</h3>;

  return (
    <div>
      <h2>Detalle del Material</h2>

      <p><strong>ID:</strong> {material.id}</p>
      <p><strong>Tipo:</strong> {material.tipo}</p>
      <p><strong>Precio:</strong> ${material.precio}</p>

      <p><strong>Bolsos:</strong>  
        {material.bolsos.length > 0 ? material.bolsos.join(", ") : " Ninguno"}
      </p>

      <Link to={`/materiales/editar/${material.id}`}>Editar</Link>
      <br />
      <button 
            onClick={() => {
                if (window.confirm("¿Seguro que deseas eliminar este material?")) {
                api.delete(`/materiales/${material.id}`)
                    .then(() => {
                    alert("Material eliminado ✔");
                    window.location.href = "/materiales";
                    })
                    .catch(error => {
                    console.error("Error eliminando:", error);
                    alert("❌ Error al eliminar");
                    });
                }
            }}
            style={{ marginTop: "10px", color: "white", backgroundColor: "red" }}
            >
            Eliminar Material
        </button>

      <Link to="/materiales">Volver al listado</Link>
    </div>
  );
}

export default DetalleMaterial;
