import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../../api/axiosConfig";

function DetalleMarca() {
  const { id } = useParams();
  const [marca, setMarca] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get(`/marcas/${id}`)
      .then(response => {
        setMarca(response.data);
      })
      .catch(error => {
        console.error("Error cargando marca:", error);
      })
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <h3>Cargando marca...</h3>;
  if (!marca) return <p>No se encontró la marca.</p>;

  return (
    <div style={{ maxWidth: "700px", margin: "0 auto" }}>
      <h2>Detalles de la Marca</h2>

      <p><strong>ID:</strong> {marca.id}</p>
      <p><strong>Nombre:</strong> {marca.nombre}</p>

      <p>
        <strong>Bolsos asociados:</strong><br />
        {marca.bolsos.length
          ? marca.bolsos.join(", ")
          : "Ninguno"}
      </p>
      <Link to={`/marcas/editar/${marca.id}`}>Editar</Link>
      <br />
      <button 
            onClick={() => {
                if (window.confirm("¿Seguro que deseas eliminar este material?")) {
                api.delete(`/marcas/eliminar/${marca.id}`)
                    .then(() => {
                    alert("Material eliminado ✔");
                    window.location.href = "/materiales";
                    })
                    .catch(error => {
                    console.error("Error eliminando:", error);
                    alert("Error al eliminar");
                    });
                }
            }}
            style={{ marginTop: "10px", color: "white", backgroundColor: "red" }}
            >
            Eliminar Material
      </button>

      <Link to="/marcas">
        <button style={{ marginTop: "20px" }}>
          Volver al listado
        </button>
      </Link>
    </div>
  );
}

export default DetalleMarca;
