import React, { useEffect, useState } from "react";
import api from "../../api/axiosConfig";
import { Link } from "react-router-dom";

function BolsoList() {
  const [bolsos, setBolsos] = useState([]);
  const [loading, setLoading] = useState(true);

  const cargarBolsos = () => {
    api.get("/bolsos/all-bolsos")
      .then(response => {
        setBolsos(response.data);
      })
      .catch(error => {
        console.error("Error cargando bolsos:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  useEffect(() => {
    cargarBolsos();
  }, []);

  const eliminarBolso = (id) => {
    if (!window.confirm("¿Desea eliminar este bolso?")) return;

    api.delete(`/bolsos/eliminar/${id}`)
      .then(() => {
        alert("Bolso eliminado exitosamente");
        cargarBolsos();
      })
      .catch(error => {
        console.error("Error eliminando bolso:", error);
        alert("No se pudo eliminar");
      });
  };

  if (loading) return <h3>Cargando bolsos...</h3>;

  return (
    <div style={{ maxWidth: "900px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center" }}>Listado de Bolsos</h2>

      <div style={{ textAlign: "right", marginBottom: "15px" }}>
        <Link to="/bolsos/crear">
          <button
            style={{
              padding: "8px 16px",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer"
            }}
          >
            Crear Nuevo Bolso
          </button>
        </Link>
      </div>

      {bolsos.length === 0 ? (
        <p>No hay bolsos registrados.</p>
      ) : (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            backgroundColor: "#f9f9f9"
          }}
        >
          <thead>
            <tr style={{ backgroundColor: "#4CAF50", color: "white" }}>
              <th style={th}>ID</th>
              <th style={th}>Modelo</th>
              <th style={th}>Marca</th>
              <th style={th}>Color</th>
              <th style={th}>Tamaño</th>
              <th style={th}>Precio</th>
              <th style={th}>Materiales</th>
              <th style={th}>Acciones</th>
            </tr>
          </thead>

          <tbody>
            {bolsos.map(bolso => (
              <tr key={bolso.id} style={tr}>
                <td style={td}>{bolso.id}</td>
                <td style={td}>{bolso.modelo}</td>
                <td style={td}>{bolso.marca}</td>
                <td style={td}>{bolso.color}</td>
                <td style={td}>{bolso.tamanio}</td>
                <td style={td}>${bolso.precio}</td>
                <td style={td}>
                  {bolso.materiales.length > 0
                    ? bolso.materiales.join(", ")
                    : "Ninguno"}
                </td>

                <td style={td}>
                  <Link to={`/bolsos/${bolso.id}`}>
                    <button style={btnVer}>Ver</button>
                  </Link>

                  <Link to={`/bolsos/editar/${bolso.id}`}>
                    <button style={btnEditar}>Editar</button>
                  </Link>

                  <button
                    onClick={() => eliminarBolso(bolso.id)}
                    style={btnEliminar}
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

// --- Estilos reutilizables ---
const th = { padding: "10px", border: "1px solid #ddd", textAlign: "center" };
const td = { padding: "10px", border: "1px solid #ddd", textAlign: "center" };
const tr = { backgroundColor: "white" };

const btnVer = {
  marginRight: "5px",
  padding: "5px 10px",
  backgroundColor: "#2196F3",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

const btnEditar = {
  marginRight: "5px",
  padding: "5px 10px",
  backgroundColor: "#FFC107",
  color: "black",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

const btnEliminar = {
  padding: "5px 10px",
  backgroundColor: "#F44336",
  color: "white",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer"
};

export default BolsoList;
